import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate, useParams } from "react-router-dom";

function CrudDetails(props) {
	const [emp, setCrud] = useState({});

	const { _id } = useParams();
	const navigate = useNavigate();

	useEffect(
		function () {
			async function getCrudById() {
				try {
					const response = await axios.get(`https://assignment2-101329235-comp3123.herokuapp.com/api/emp/employees/${_id}`);
					setCrud(response.data);
				} catch (error) {
					console.log("error", error);
				}
			}
			getCrudById();
		}, [props]
	);

	async function handleDelete() {
		try {
			await axios.delete(`https://assignment2-101329235-comp3123.herokuapp.com/api/emp/employees/${_id}`);
			navigate("/employee");
		} catch (error) {
			console.error(error);
		}
	}

	return (
		<div className="container">
			<h1>Employee Details</h1>
			<hr/>
			<p>
				<b>First Name</b>: {emp.first_name}
			</p>
			<p>
				<b>Last Name</b>: {emp.last_name}
			</p>
			<p>
				<b>Email</b>: {emp.email}
			</p>

			<p>
				<small>
					<b>ID</b>: {emp._id}
				</small>
			</p>
			<div className="btn-group ">
				<Link to={`/employee/${emp._id}/edit`} className="btn btn-primary">
					Edit
				</Link>
				<button onClick={handleDelete} className="btn btn-danger">
					Delete
				</button>
				<Link to="/employee" className="btn btn-secondary">
					Close
				</Link>
			</div>
			<hr />
			<div>
				Copyright Â© <small>{new Date().getFullYear()}</small> - Ellyn Bibon{" "}
			</div>
		</div>
	);
}

export default CrudDetails;
