import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function CrudTable() {
	const [cruds, setCruds] = useState([]);

	useEffect(function () {
		async function getCruds() {
			try {
				const response = await axios.get("https://assignment2-101329235-comp3123.herokuapp.com/api/emp/employees");
				setCruds(response.data);
			} catch (error) {
				console.log("error", error);
			}
		}
		getCruds();
	}, []);

	return (
		<div style={{ backgroundColor: 'purple', height:500}}>
		<div className="container">
			<div>
				<h2>
					Employee - Table View
					<p>
						<Link to="/employee/new" className="btn btn-primary float-right">
							Create Employee
						</Link>
					</p>
				</h2>
				<hr />
			</div>

            <div className="table-responsive">
			<table className="table table-striped table-dark">
				<thead>
					<tr>
						<th>First Name</th>
						<th>Last Name</th>
						<th>Email</th>
						<th>View</th>
						<th>Edit</th>
						<th>Delete</th>
					</tr>
				</thead>
				<tbody>
					{cruds &&
						cruds.map((crud) => {
							return (
								<tr key={crud._id}>
									<td>
										<Link to={`/employee/${crud._id}`} className="link-line">
											{crud.first_name}
										</Link>
									</td>
									<td>{crud.last_name}</td>
									<td>{crud.email}</td>
									<td>
										<Link to={`/employee/${crud._id}`} className="btn btn-warning">
											View
										</Link>
									</td>
									<td>
										<Link
											to={`/employee/${crud._id}/edit`}
											className="btn btn-success"
										>
											Edit
										</Link>
									</td>
									<td>
										<Link
											to={`/employee/${crud._id}/delete`}
											className="btn btn-danger"
										>
											Delete
										</Link>
									</td>
								</tr>
							);
						})}
				</tbody>
			</table>
			</div>
		</div>
		</div>
	);
}

export default CrudTable;
